import React from 'react';

export default function Hero() {
  return (
    <div className='relative text-center'>
      <img 
        src="images/safe.jpg" // Replace with your actual image URL
        alt="Safe Drinking Water"
        className='w-full h-auto object-cover'
      />
      <div className='absolute top-0 left-0 w-full h-full flex flex-col justify-center items-center bg-black bg-opacity-50 text-white'>
        <h2 className='text-4xl font-bold mb-2'>
          Safe and Pure Drinking Water
        </h2>
        <p className='text-xl'>
          Purity levels at <span className='font-bold text-blue-600'>99.9%</span>.
        </p>
      </div>
    </div>
  );
}
